import React from "react";
import { Link } from "react-router-dom";
import "../ProjectsNavigation/MediaQueries.css";
import { useEffect } from "react";
import { useState } from "react";
import { useLocation } from "react-router-dom";


const ProjectsNavigation = () => {
    const [selectedOption, setSelectedOption] = useState(null);

    useEffect(() => {
        const currentPath = window.location.pathname;
        if (currentPath === "/") {
            setSelectedOption(1);
        } else if (currentPath === "/uxProjects") {
            setSelectedOption(2);
        } else if (currentPath === "/frontendProjects") {
            setSelectedOption(3);
        }
    }, [location.pathname]);

    const handleOptionClick = (option) => {
        setSelectedOption(option);
    };

    return (

        <div className="navigationContainer">
        <ul>
                 <li>
                    <Link to="/" className={`noUnderline ${selectedOption === 1 ? 'selected' : ''}`} onClick={() => handleOptionClick(1)}>
                        <span >Todos</span>
                    </Link>
                </li> 
                <li>
                    <Link to="/uxProjects" className={`noUnderline ${selectedOption === 2 ? 'selected' : ''}`} onClick={() => handleOptionClick(2)}>
                        <span>User Experience</span>
                    </Link>
                </li>   
                <li >
                    <Link to="/frontendProjects" className={`noUnderline ${selectedOption === 3 ? 'selected' : ''}`} onClick={() => handleOptionClick(3)}>
                        <span>Front-End</span>
                    </Link>

                </li>             
            </ul>
        </div>
    )
}


export default ProjectsNavigation;
